import { MergeClient } from "../../../src/Client";
import { mockServerPool } from "../../mock-server/MockServerPool";

describe("PhoneNumbersClient", () => {
    test("retrieve", async () => {
        const server = mockServerPool.createServer();
        const client = new MergeClient({ apiKey: "test", accountToken: "test", environment: server.baseUrl });

        const rawResponseBody = {
            created_at: "2021-09-15T00:00:00Z",
            modified_at: "2021-10-16T00:00:00Z",
            number: "+3198675309",
            type: "Mobile",
        };
        server
            .mockEndpoint()
            .get("/accounting/v1/phone-numbers/id")
            .respondWith()
            .statusCode(200)
            .jsonBody(rawResponseBody)
            .build();

        const response = await client.accounting.phoneNumbers.retrieve("id", {
            includeRemoteData: true,
            includeShellData: true,
        });
        expect(response).toEqual({
            createdAt: new Date("2021-09-15T00:00:00.000Z"),
            modifiedAt: new Date("2021-10-16T00:00:00.000Z"),
            number: "+3198675309",
            type: "Mobile",
        });
    });
});
